
CREATE TABLE foto (
    id int NOT NULL AUTO_INCREMENT,
    foto longblob NOT NULL,
    CONSTRAINT foto_pkey PRIMARY KEY (id)
);

CREATE TABLE usuario (
    id int NOT NULL AUTO_INCREMENT,
    login varchar(255) NOT NULL,
    nome varchar(255),
    papel tinyint default 0,
    senha varchar(255),
    foto int,
    CONSTRAINT usuario_pkey PRIMARY KEY (id)
);

